mapper.insert:
INSERT INTO tbl_files ( FILE_SEQ, FILE_P_CODE, FILE_ORIGIN_NAME, FILE_UPLOAD_NAME ) SELECT SEQ_FILES.NEXTVAL, SUB.* FROM (
SELECT #{p_code}, #{file.file_origin_name}, #{file.file_upload_name} FROM DUAL
) SUB

mapper.select:
